const [person, setPerson] = useState(obj);

const Temp1 = () => {
  const obj = {
    name: "Alan Smith",
    age: 29,
    height: 5.33,
    isYoung: true,
  };

  let changeState = () => {
    setPerson({
      name: "Bob Smith",
      age: 45,
      height: 5.4,
      isYoung: false,
    });
  };

  return (
    <div>
      <h1>{person.name}</h1>
      <h1>{person.age}</h1>
      <h1>{person.height}</h1>
      <h1>{person.isYoung.toString()}</h1>
      <button onClick={changeState}>Change</button>
    </div>
  );
};

export default Temp1;
