import PropTypes from "prop-types";

const Header = ({ title }) => {
  return (
    <header className="header">
      <h1 style={headingStyle}>{title}</h1>
      <button>Add</button>
    </header>
  );
};

Header.defaultProps = {
  title: "Task Tracker",
};

Header.propTypes = {
  title: PropTypes.string.isRequired,
};

